// These constants represent the RISC-V ELF and the image ID generated by risc0-build.
// The ELF is used for proving and the ID is used for verification.
use methods::{MATMUL_ELF, MATMUL_ID};
use risc0_zkvm::{default_prover, ExecutorEnv};
use serde::{Deserialize, Serialize};
use half::bf16;

#[derive(Serialize, Deserialize)]
struct DotProductInput {
    column: Vec<u16>,
    vector: Vec<u16>,
}

fn main() {
    // Initialize tracing. In order to view logs, run `RUST_LOG=info cargo run`
    tracing_subscriber::fmt()
        .with_env_filter(tracing_subscriber::filter::EnvFilter::from_default_env())
        .init();

    // Generate sample input data: a column vector and a vector for dot product
    // Using 4096 elements to simulate a large matrix operation (Qwen3 default)
    let size = 4096;
    let column: Vec<u16> = (0..size)
        .map(|i| bf16::from_f32((i as f32) * 0.001).to_bits())
        .collect();
    let vector: Vec<u16> = (0..size)
        .map(|i| bf16::from_f32((i as f32) * 0.002 + 1.0).to_bits())
        .collect();

    let input = DotProductInput { column, vector };

    // Create executor environment with the input
    let env = ExecutorEnv::builder()
        .write(&input)
        .unwrap()
        .build()
        .unwrap();

    // Obtain the default prover.
    let prover = default_prover();

    // Proof information by proving the specified ELF binary.
    // This struct contains the receipt along with statistics about execution of the guest
    let prove_info = prover
        .prove(env, MATMUL_ELF)
        .unwrap();

    // Extract the receipt.
    let receipt = prove_info.receipt;

    // Decode and print the dot product result from the receipt journal
    let output: f32 = receipt.journal.decode().unwrap();
    println!("Dot product result: {}", output);

    // The receipt was verified at the end of proving, but the below code is an
    // example of how someone else could verify this receipt.
    receipt
        .verify(MATMUL_ID)
        .unwrap();
}
